import React, { useContext } from 'react';
import ToDoContext from './context/ToDoContext';

function List() {
    const { tasks, removeTask } = useContext(ToDoContext);
    return (
        <div>
           { tasks.map((task) => (<p onClick={(task) => removeTask(task) }>{ task }</p>)) }
        </div>
    )
}

export default List;
